import React from 'react';
import attendanceService from '../services/attendanceService';
import { AuthContext } from '../context/authContext';
import { BoxLoading } from 'react-loadingg';
import moment from 'moment';

export default function Home() {
	const [isMarked, setIsMarked] = React.useState(false);
	const [load, setLoad] = React.useState(true);
	const { user } = React.useContext(AuthContext);

	/** COMPONENT_DID_MOUNT START */
	React.useEffect(() => {
		const data = { date: moment(new Date()).format('YYYY-MM-DD'), id: user.id };
		attendanceService
			.checkIfAttendanceMarked(data)
			.then((resData) => {
				console.log(resData);
				setIsMarked(resData.data);
			})
			.catch((err) => {
				console.log(err);
				setLoad(true);
			});
		return () => {
			setIsMarked(false);
			setLoad(true);
		};
	}, []);
	/** COMPONENT_DID_MOUNT END */

	const markAttendance = () => {
		const data = { date: moment(new Date()).format('YYYY-MM-DD'), id: user.id };
		attendanceService
			.markAttendance(data)
			.then((resData) => {
				console.log(resData);
			})
			.catch((err) => {
				console.log(err);
			});
	};
	return (
		<>
			{load ? (
				<div className='text-center'>
					{isMarked ? (
						<>you attendance for today has been already marked</>
					) : (
						<button className='btn btn-success btn-sm' onClick={markAttendance}>
							Mark Attendance
						</button>
					)}
				</div>
			) : (
				<>
					<BoxLoading />
				</>
			)}
		</>
	);
}
