import React from 'react'
import attendanceService from '../services/attendanceService';
import {AuthContext} from '../context/authContext';
import moment from 'moment';

export default function Home() {
    const {user}=React.useContext(AuthContext);

    const markAttendance=()=>{

    const data={date:moment(new Date()).format("YYYY-MM-DD HH:MM:SS"),id:user.id}
    attendanceService.markAttendance(data).then((resData)=>{
        console.log(resData)
    }).catch((err)=>{
        console.log(err)
    })

    }
    return (
        <div>
            <button className="btn btn-success btn-sm" onClick={markAttendance}>Mark Attendance</button>
        </div>
    )
}
