import React from 'react'
import attendanceService from '../services/attendanceService';
import {AuthContext} from '../context/authContext';
import moment from 'moment';

export default function Home() {
    const [isMarked,setIsMarked]=React.useState(false)
    const {user}=React.useContext(AuthContext);

    /** COMPONENT_DID_MOUNT START */
    React.useEffect(()=>{
        const data={date:moment(new Date()).format("YYYY-MM-DD"),id:user.id}
        attendanceService.checkIfAttendanceMarked(data).then((resData)=>{
            console.log(resData)
            setIsMarked(resData.data)
        }).catch((err)=>{
            console.log(err)
        })
    },[])
     /** COMPONENT_DID_MOUNT END */



    const markAttendance=()=>{
    const data={date:moment(new Date()).format("YYYY-MM-DD"),id:user.id}
    attendanceService.markAttendance(data).then((resData)=>{
        console.log(resData)
    }).catch((err)=>{
        console.log(err)
    })

    }
    return (
        <div>
            {isMarked?(<>you attendance for today has been already marked</>):( <button className="btn btn-success btn-sm" onClick={markAttendance}>Mark Attendance</button>)}

        </div>
    )
}
