import React from 'react';
import { useForm, useFieldArray } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers';
import * as yup from 'yup';
import userService from '../services/userService';
import './LoginPage.css';

export default function LoginPage() {
	/** HANDLE FORM SUBMIT START * */
	const validationSchema = yup.object().shape({
		email: yup.string().email().required('Email is required'),
		password: yup
			.string()
			.min(8, 'Password must be at least 6 characters')
			.required('Password is required'),
	});
	/** HANDLE FORM SUBMIT END * */

	const { register, errors, handleSubmit, getValues } = useForm({
		resolver: yupResolver(validationSchema),
	});
	React.useEffect(() => {
		console.log(errors);
	}, [errors]);

	/** HANDLE FORM SUBMIT START * */
	const onSubmit = (formData) => {
		console.log(formData);
		// userService.login({email:formData.email,password:formData.password}).then((resData)=>{
		//     console.log("",resData)
		//     const {success,data,message}=resData;
		//     if(success){}
		//     else{}
		// }).catch((err)=>{
		//     console.log(err)
		// })
	};
	/** HANDLE FORM SUBMIT END * */
	return (
		<div>
			<section>
				<hgroup>
					<h2>Welcome back!</h2>
					<p>Please enter your details to sign into your account</p>
				</hgroup>

				<form className='log-form' onSubmit={handleSubmit(onSubmit)}>
					<div className='group log-input'>
						<input
							{...register('email', { required: true })}
							type='text'
							id='email'
							name='email'
							placeholder='email'
						/>

					</div>
					{errors?.email ? <small className="font-weight-bold text-danger">{errors?.email.message}</small> : <></>}


					<div className='group log-input'>
						<input
							{...register('password', { required: true })}
							type='password'
							id='password'
							name='password'
							placeholder='Password'
						/>
						{errors?.passsword ? <small>{errors?.passsword.message}</small> : <></>}
					</div>



									<br />

					<div className='container-log-btn'>
						<button type='submit' name='btn_submit' className='log-form-btn'>
							<span>Login</span>
						</button>
					</div>
				</form>
			</section>
		</div>
	);
}
