import React from 'react';
import { useForm } from 'react-hook-form';
import {BoxLoading} from 'react-loadingg'
import { yupResolver } from '@hookform/resolvers';
import * as yup from 'yup';
import {NotificationContainer, NotificationManager} from 'react-notifications';
import userService from '../services/userService';
import './LoginPage.css';
import 'react-notifications/lib/notifications.css';
import {AuthContext} from '../context/authContext.js'
export default function LoginPage() {

	const [load,setLoad]=React.useState(true);
	const {setIsAuthenticated,setUser}=React.useContext(AuthContext);





	/** COMPOENENT_DID_MOUNT && COMPONENT_DID_UNMOUNT START * */
	React.useEffect(()=>{
		setLoad(true);
	return ()=>{
		setLoad(true);
	}
	},[setLoad])
	/** COMPOENENT_DID_MOUNT && COMPONENT_DID_UNMOUNT END * */





	/** HANDLE FORM SUBMIT START * */
	const validationSchema = yup.object().shape({
		email: yup.string().email().required('Email is required'),
		password: yup
			.string()
			.required('Password is required')
			.min(6, 'Password must be at least 6 characters')
			.max(14, 'Password must be at most 14 characters'),
	});
	/** HANDLE FORM SUBMIT END * */

	const { register, errors, handleSubmit, reset } = useForm({
		resolver: yupResolver(validationSchema),
		defaultValues:{
			email:'admin@admin.com',
			password:'admin@123'
		}
	});
	React.useEffect(() => {
		console.log(errors);
	}, [errors]);

	/** HANDLE FORM SUBMIT START * */
	const onSubmit = (formData) => {
		userService.login({email:formData.email,password:formData.password}).then((resData)=>{
		if(resData.status!==401){
			const {success,data,message}=resData;
		    if(success){
				NotificationManager.success(message);
				setIsAuthenticated(true);
				setUser({username:data.username,email:data.email,status:data.status})
			}
		    else{
				NotificationManager.warning("in valid credentails ...")
			}
		}
		else{

		}

		}).catch((err)=>{
		    console.log(err);

		})
	};
	/** HANDLE FORM SUBMIT END * */
	return (
		<div>
			{load ? (	<section>
				<hgroup>
					<h2>Welcome back!</h2>
					<p>Please enter your details to sign into your account</p>
				</hgroup>

				<form className='log-form' onSubmit={handleSubmit(onSubmit)}>
					<div className='group log-input'>
						<input ref={register} type='text' id='email' name='email' placeholder='email' />
					</div>
					{errors?.email ? (
						<small className='font-weight-bold text-danger'>{errors?.email.message}</small>
					) : (
						<></>
					)}

					<div className='group log-input'>
						<input
							ref={register}
							type='password'
							id='password'
							name='password'
							placeholder='Password'
						/>
					</div>
					{errors?.password ? (
						<small className='font-weight-bold text-danger'>{errors?.password.message}</small>
					) : (
						<></>
					)}

					<br />

					<div className='container-log-btn'>
						<button type='submit' name='btn_submit' className='log-form-btn'>
							<span>Login</span>
						</button>
					</div>
				</form>
			</section>):(<BoxLoading/>)}
		<NotificationContainer/>
		</div>
	);
}
