import React from 'react';
import { AuthContext } from '../context/authContext';
import { useForm } from 'react-hook-form';
import { BoxLoading } from 'react-loadingg';
import MuiDataTable from 'mui-datatables';
import { yupResolver } from '@hookform/resolvers';
import * as yup from 'yup';
import attendanceService from '../services/attendanceService';
import { NotificationManager } from 'react-notifications';
import * as FaIcons from 'react-icons/fa';
export default function AttendancePage() {
	const [load, setLoad] = React.useState(true);
	const [state, setState] = React.useState([]);
	const { user } = React.useContext(AuthContext);

	/** FORM_VALIDATION START * */
	const validationSchema = yup.object().shape({
		email: yup.string().email().required('Email is required'),
	});
	/** FORM_VALIDATION END * */

	const { register, handleSubmit, reset } = useForm({
		resolver: yupResolver(validationSchema),
		defaultValues: {
			email: 'admin@admin.com',
		},
	});

	/** COMPONENT_DID_MOUNT START */
	React.useEffect(() => {
		return () => {
			setLoad(true);
		};
	}, []);
	/** COMPONENT_DID_MOUNT START */

	/** HANDLE FORM SUBMIT START * */
	const onSubmit = (formData) => {
    console.log("asd")
		setLoad(false);
		attendanceService
			.getAttendanceByEmail({ email: formData.email })
			.then((resData) => {
				console.log(resData);
				if (resData.success) {
					NotificationManager.success('request successfull');
					setState(resData.data);
					console.log(resData.data);
				}
				setLoad(true);
			})
			.catch((err) => {
				console.log(err);
				setLoad(true);
			});
	};
	/** HANDLE FORM SUBMIT END * */
	return (
		<div className='conatiner-fluid'>
			<div className='row'>
				{state && state.length === 0 ? (
		<form onSubmit={handleSubmit(onSubmit)}>
						<div className='col-md-4'></div>
						<div className='col-md-4'>
							<div className='card'>
								<div className='card-header text-center'>
									<span className='text-dark'>View Attendance</span>
								</div>
								<div className='card-body'>

										<label htmlFor='email' className='text-dark p-1'>
											Email
										</label>
										<input
											type='email'
											name='email'
											id='email'
											ref={register}
											className='form-control'
										/>

								</div>
								<div className='card-footer text-center'>
									<button type='submit' className='btn btn-sm btn-success'>
										<FaIcons.FaSearch /> View Attendance
									</button>
								</div>
							</div>
						</div>
						<div className='col-md-4'></div>
            </form>
				) : (
					<>
						<MuiDataTable
							title={`${state[0]?.username} attendance`}
							data={state || []}
							/**
							 * EXAMPLE COLUMN RENDERING
							 * https://github.com/gregnb/mui-datatables/blob/master/examples/component/index.js
							 */
							columns={[
								{
									name: 'id',
									label: 'Id',
									options: {
										display: 'false',
									},
								},
								{
									name: 'email',
									label: 'Email',
									options: {
										display: 'true',
									},
								},
								{
									name: 'username',
									label: 'Username',
									options: {
										display: 'true',
									},
								},

								{
									name: 'date',
									label: 'Date',
									options: {
										display: 'true',
									},
								},

								{
									name: 'isMarked',
									label: 'is Marked',
									options: {
										display: 'true',
										customBodyRender: (value) => (
											<span className={`font-weight-bold text-${value}?"success":"danger"`}>
												{value === 1 ? 'present' : 'absent'}
											</span>
										),
									},
								},
							]}
						/>
						<button type='button' className='btn btn-success btn-sm' onClick={() => setState([])}>
							View Again
						</button>
					</>
				)}
			</div>
		</div>
	);
}
